    private void btnIniciarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIniciarActionPerformed
        //configuro mi productor
        modeloDeProductor= new ModeloListaPropio(this.lblProductorCuantos);
        int cuantos = Integer.parseInt(this.spnProductorCuantos.getValue().toString());
        for(int i=0; i < cuantos;i++)
        {
         modeloDeProductor.addElement("producto nÂ°" + i);   
        }
        this.lstProductor.setModel(modeloDeProductor);
        
        //configuro mi consumidor
        modeloDeConsumidor= new ModeloListaPropio(this.lblConsumidorCuantos);
        this.lstConsumidor.setModel(modeloDeConsumidor);
        
        //configuro el buffer
        int tamanoBuffer = Integer.parseInt(this.spnBufferCapacidad.getValue().toString());
        modeloDeBuffer = new Buffer(tamanoBuffer, this.lblBufferCuantos);
        this.lstBuffer.setModel(modeloDeBuffer);
        //lanzar los hilos
        
        long tiempoProductor = Long.parseLong(this.spnProductorVelocidad.getValue().toString());
        Thread hiloProductor = new Thread(new Productor(this, modeloDeBuffer , tiempoProductor));
        hiloProductor.start();

        long tiempoConsumidor = Long.parseLong(this.spnConsumidorVelocidad.getValue().toString());
         Thread hiloConsumidor = new Thread(new Consumidor(this, modeloDeBuffer,tiempoConsumidor));
         hiloConsumidor.start();
    }//GEN-LAST:event_btnIniciarActionPerformed

